void putfont8(uint8 *vram, int xsize, int x, int y, uint8 c, uint8 *font) {
  int i;
  uint8 *p, d;
  for(i = 0; i < 16; i++) {
    p = vram + (y + i) * xsize + x;
    d = font[i];
    if((d & 0x80) != 0) p[0] = c;
    if((d & 0x40) != 0) p[1] = c;
    if((d & 0x20) != 0) p[2] = c;
    if((d & 0x10) != 0) p[3] = c;
    if((d & 0x08) != 0) p[4] = c;
    if((d & 0x04) != 0) p[5] = c;
    if((d & 0x02) != 0) p[6] = c;
    if((d & 0x01) != 0) p[7] = c;
  }
}

